var util = require('util');

exports = module.exports = require('./Client_types');

var Hql_types = require('./Hql_types');
exports.HqlResult = Hql_types.HqlResult;
exports.HqlResult2 = Hql_types.HqlResult2
exports.HqlResultAsArrays = Hql_types.HqlResultAsArrays;

exports.thriftClient = require('./thriftClient');

exports.printCell = function (cell) {
  console.log(util.format("{Cell: {Key row='%s' cf='%s' cq='%s' ts=%d revision=%s flag=%d} %s}",
                          cell.key.row, cell.key.column_family,
                          cell.key.column_qualifier,
                          cell.key.timestamp.toNumber(true),
                          cell.key.revision.toNumber(true),
                          cell.key.flag),
                          cell.value);
}